import axios from "axios";

const incr = () => {
  return {
    type: "increment",
  };
};
export default incr;

export const decr = () => (dispatch) => {
  dispatch({
    type: "decrement",
  });
};

export const amount = (data) => {
  console.log("ddd", data);
  return {
    type: "amount",
    data: data,
  };
};
