export function amount() {
  return function async(dispatch) {
    return axios
      .get("https://run.mocky.io/v3/9d71cb03-a9f9-4d70-bae2-9d3adaa1cfe7")
      .then((res) => {
        dispatch({
          type: amount,
        });
      });
  };
}
