export default function reducer(state = [], action) {
  switch (action.type) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    case "amount":
      console.log("red", action.payload);
      return [...state, action.payload];

    default:
      return state;
  }
}
