import axios from "axios";

const incr = () => {
  return {
    type: "increment",
  };
};
export default incr;

export const decr = () => async (dispatch) => {
  dispatch({
    type: "decrement",
  });
};

export const amount = async () => {
  let response;
  await axios
    .get("https://run.mocky.io/v3/9d71cb03-a9f9-4d70-bae2-9d3adaa1cfe7")
    .then((res) => (response = res.data))
    .catch((err) => console.log("error"));

  return { type: "amount", payload: response };
};
