import GET_ITEMS from "../actionType";
let items = [];
const itemReducer = (state = items, action) => {
  if (action.type == "GET_ITEMS") console.log(action);
  switch (action.type) {
    case GET_ITEMS:
      items = action.payload;
      //console.log("store", ...state, items);
      return { ...state, items };
    default:
      return state;
  }
};
export default itemReducer;
