const initialUserState = {
  arr: [],
};

export default function reducer(state = initialUserState, action) {
  switch (action.type) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    case "amount":
      return [...state, action.payload];

    default:
      return state;
  }
}
