};
export const getData = () => {
  return (dispatch) => {
    axios
      .get("https://run.mocky.io/v3/9d71cb03-a9f9-4d70-bae2-9d3adaa1cfe7")
      .then((response) => {
        dispatch(getDataSuccess(response.data));
      })
      .catch((error) => {
        //TODO: handle the error when implemented
      });
  };
};
