const count = 5;

export default function reducer(state = count, action) {
  const { type, payload } = action;
  switch (type) {
    case "increment":
      return state + 1;
    case "decrement":
      return state - 1;
    case "amount":
      const data = payload;
      return [...state, data];
    default:
      return state;
  }
}
